    protected override async Task OnInitializedAsync()
    {
        var timer = new Timer(new TimerCallback(x =>
        {
            this.StateHasChanged();
        }), DateTime.Now, 100, 100);

        if (await localStore.ContainKeyAsync("displayed"))
        {
            Data.Displayed = await localStore.GetItemAsync<List<TodoCatagoryModel>>("displayed");
        }
        else
        {
            await localStore.SetItemAsync("displayed", Data.Displayed);
        }

        if (await localStore.ContainKeyAsync("stored"))
        {
            Data.Stored = await localStore.GetItemAsync<List<TodoCatagoryModel>>("stored");
        }
        else
        {
            await localStore.SetItemAsync("stored", Data.Stored);
        }

        await base.OnInitializedAsync();
    }

    private async Task AddTodo()
    {
        if (Data.Todo.Text != "" && Data.Todo.CatagoryName != "")
        {
            Data.Todo.TimeAdded = DateTime.Now.AddHours(5.5);
            Data.Displayed
                .Find(x => x.Name == Data.Todo.CatagoryName)
                .Todos.Add(Data.Todo);

            await localStore.SetItemAsync("displayed", Data.Displayed);

            Data.Todo = new TodoClass();
        }
    }

    private async Task CheckKeyPress(KeyboardEventArgs args)
    {
        switch (args.Key)
        {
            case "Enter":
                await AddTodo();
                break;

            default:
                return;
        }
    }

    private void ShowAddCatagory()
    {
        Data.ShowCatagory = true;
    }

